scripts.script-block:

$.widget("ibi.minesweeper", $.ibi.ibxVBox, 
{
	options:
	{
		nameRoot:true,
		align:"stretch",
		nMines:99,
		boardHeight:16,
		boardWidth:30,
	},
	_widgetClass:"mine-sweeper",
	_minesRemaining:0,
	_gameTime:0,
	_timer:null,
	_create:function()
	{
		this._super();
		var res = ibx.resourceMgr.getResource(".mine-sweeper-markup");
		this.element.append(res)
		ibx.bindElements(res);
	},
	_init:function()
	{
		this._super();
		this.newGame();
	},
	_destroy:function()
	{
		this._super();
	},
	newGame:function(options)
	{
		options = $.extend({}, this.options, options);

		this.board.empty();
		for(var i = 0; i < options.boardHeight; ++i)
		{
			var row = $("<div>").ibxFlexGrid({columnCount:options.boardWidth, wrap:false});
			this.board.append(row);
			for(var j = 0; j < options.boardWidth; ++j)
			{
				var tile = $("<div tabindex='0' class='board-tile'>").on("click", this._onTileClick.bind(this));
				row.append(tile);
			}
		}

		this._minesRemaining = options.nMines;
		this._gameTime = 0;
		this.minesRemaining.ibxWidget("option", "text", this._minesRemaining);
		this.gameTime.ibxWidget("option", "text", this._gameTime);
		this._timer = window.setInterval(this._onTick.bind(this), 1000);
	},
	_onGameOver:function(e)
	{
		window.clearInterval(this._time);
	},
	_onTileClick:function(e)
	{
	},
	_onTick:function()
	{
		this._gameTime++;
		this.gameTime.ibxWidget("option", "text", this._gameTime);
	},
	_refresh:function()
	{
		var options = this.options;
		this._super();
	}
});
//# sourceURL=minesweeper.xml
