/*	COLOR GUIDE
	Add style sheet standard colors here.
---------------------------------------------------------
COLOR:			USAGE:
darkgray		default neutral color.
crimson			error/high priority color.
orange			warning/medium priority color.
forestgreen		OK priority color.
royalblue		informational color.
****/

body
{
	font-size:12px;
	font-family: 'Arial', sans-serif;
	-webkit-tap-highlight-color: rgba(0, 0, 0, 0);
	cursor:default;
}

/*hide from here down so we can load/bind ibx before showing markup...usually added to <body>*/
.ibx-root
{
	visibility:hidden;
}

/*ibx is fully loaded*/
.ibx-loaded
{
}

/*any ibx root nodes will no longer be explicitly hidden*/
.ibx-root.ibx-visible
{
	visibility:visible;
}

/*when dragging, stop iframes from eating the mouse events*/
.ibx-dragging iframe
{
	pointer-events:none;
}

.ibx-drag-image
{
	pointer-events:none;
	opacity:.5;
	z-index:2147483647;/*max z-index*/
}

input
{
  font: inherit;
  margin: 0px;
  padding:0px;
}
/*chrome turns off the focus outline when readonly...not sure why, but this is better.*/
/*[PD-762] they don't want this behavior.
input:read-only:focus
{
	outline:auto;
	outline-color:-webkit-focus-ring-color;
	opacity:.5;
}
*/

textarea {
    font: inherit;
