/*Container Type*/
.ibx-flexbox.fbx-block
{
	display:-webkit-box;
	display:-moz-box;
	display:-ms-flexbox;
	display:-webkit-flex;
	display:flex;
}
.ibx-flexbox.fbx-inline
{
	display:-webkit-box;
	display:-moz-box;
	display:-ms-inline-flexbox;
	display:-webkit-inline-flex;
	display:inline-flex;
}

/*Orientation*/
.ibx-flexbox.fbx-row
{
	-webkit-box-direction:normal;
	-webkit-box-orient:horizontal;
	-moz-box-direction:normal;
	-moz-box-orient:horizontal;
	-webkit-flex-direction:row;
	-ms-flex-direction:row;
	flex-direction:row;
}
.ibx-flexbox.fbx-row-reverse
{
	-webkit-box-direction:reverse;
	-webkit-box-orient:horizontal;
	-moz-box-direction:reverse;
	-moz-box-orient:horizontal;
	-webkit-flex-direction:row-reverse;
	-ms-flex-direction:row-reverse;
	flex-direction:row-reverse;
}
.ibx-flexbox.fbx-column
{
	-webkit-box-direction:normal;
	-webkit-box-orient:vertical;
	-moz-box-direction:normal;
	-moz-box-orient:vertical;
	-webkit-flex-direction:column;
	-ms-flex-direction:column;
	flex-direction:column;
}
.ibx-flexbox.fbx-column-reverse
{
	-webkit-box-direction:reverse;
	-webkit-box-orient:vertical;
	-moz-box-direction:reverse;
	-moz-box-orient:vertical;
	-webkit-flex-direction:column-reverse;
	-ms-flex-direction:column-reverse;
	flex-direction:column-reverse;
}

/*Wrapping*/
.ibx-flexbox.fbx-nowrap
{
	-webkit-flex-wrap:nowrap;
	-ms-flex-wrap:nowrap;
	flex-wrap:nowrap;
}
.ibx-flexbox.fbx-wrap
{
	-webkit-flex-wrap:wrap;
	-ms-flex-wrap:wrap;
	flex-wrap:wrap;
}
.ibx-flexbox.fbx-wrap-reverse
{
	-webkit-flex-wrap:wrap-reverse;
	-ms-flex-wrap:wrap-reverse;
	flex-wrap:wrap-reverse;
}

/*Justification or Packing of content*/
.ibx-flexbox.fbx-justify-content-start
{
	-webkit-box-pack:start;
	-moz-box-pack:start;
	-webkit-justify-content:flex-start;
	-ms-flex-pack:start;
	justify-content:flex-start;
}
.ibx-flexbox.fbx-justify-content-end
{
	-webkit-box-pack:end;
	-moz-box-pack:end;
	-webkit-justify-content:flex-end;
	-ms-flex-pack:end;
	justify-content:flex-end;
}
.ibx-flexbox.fbx-justify-content-center
{
	-webkit-box-pack:center;
	-moz-box-pack:center;
	-webkit-justify-content:center;
	-ms-flex-pack:center;
	justify-content:center;
}
.ibx-flexbox.fbx-justify-content-space-between
{
	-webkit-box-pack:justify;
	-moz-box-pack:justify;
	-webkit-justify-content:space-between;
	-ms-flex-pack:justify;
	justify-content:space-between;
}
