scripts.script-block:

$.widget("ibi.testFrame", $.ibi.ibxVBox, 
{
	options:
	{
		"template":".test-frame",
	},
	_widgetClass:"test-frame",
	_create:function()
	{
		this._super();
	},
	_init:function()
	{
		this._super();
		var container = $(".test-content");
		for(var i = 0; i < 50; ++i)
		{
			var item = $("<div tabindex='0'>").ibxLabel({"text":"Item" + i, "draggable":true, "align":"center", "justify":"center"});
			item.addClass("test-item item"+i);
			container.append(item);
		};
	},
	_destroy:function()
	{
		this._super();
	},
	_refresh:function()
	{
		var options = this.options;
		this._super();
	}
});
