scripts.script-block:

$.widget("ibi.testWidget", $.ibi.ibxVBox, 
{
	options:
	{
		align:"stretch"
	},
	_widgetClass:"test-widget",
	_create:function()
	{
		this._super();
		var res = ibx.resourceMgr.getResource("widget-resources", false);
		var children = res.children();
		this.element.append(children);
		ibx.bindElements(this.element);
	},
	_destroy:function()
	{
		this._super();
	},
	_refresh:function()
	{
		this._super();
	}
});
